INSERT INTO MOVIE_BASIC_INFO (MOVIE_ID, MOVIE_NAME, MOVIE_ENGLISH_NAME, MOVIE_COUNTRY, MOVIE_DB_SCORE, MOVIE_CONTENT, MOVIE_SHOW_TIME, CRT_DATE, ALT_DATE) VALUES ('0cae0916-0c68-4d50-b165-08cbe2cad675', '熔炉', 'Silenced', '韩国', '9.3', '本片取材于2005年光州一所聋哑障碍人学校的真实事件，改编自韩国作家孔枝泳的同名小说。', '2011-09-22', TO_DATE('2019-07-14 19:06:19', 'YYYY-MM-DD HH24:MI:SS'), TO_DATE('2019-07-14 19:06:19', 'YYYY-MM-DD HH24:MI:SS'));
INSERT INTO MOVIE_BASIC_INFO (MOVIE_ID, MOVIE_NAME, MOVIE_ENGLISH_NAME, MOVIE_COUNTRY, MOVIE_DB_SCORE, MOVIE_CONTENT, MOVIE_SHOW_TIME, CRT_DATE, ALT_DATE) VALUES ('22999d08-b102-475c-9e46-7061b5e3b0da', '恐怖直播', 'The Terror Live', '韩国', '8.7', '韩国首尔某个平凡的早上，广播电台Daily Topic节目的主持人尹英华（河正宇 饰）正就税率改革与听众进行连线，这时一个自称住在首尔昌信洞的普通工人朴鲁圭打入电话，大肆抱怨超高的电费和相关部门对他的威胁。尹英华颇不耐烦，以偏离主题为由强行切断电话。谁知连线无法单方面由电台方面中断，愤怒的朴鲁圭继续抱怨，并扬言炸掉汉江大桥。尹英华不以为然，进而怂恿对方想做就做，谁知话音刚落，窗外的麻浦大桥便炸作废墟。大惊失色的尹英华很快冷静下来，他决定利用这次难得的机会打一次事业的翻身仗。 ', '2013-07-31', TO_DATE('2019-07-14 19:06:19', 'YYYY-MM-DD HH24:MI:SS'), TO_DATE('2019-07-14 19:06:19', 'YYYY-MM-DD HH24:MI:SS'));
INSERT INTO MOVIE_BASIC_INFO (MOVIE_ID, MOVIE_NAME, MOVIE_ENGLISH_NAME, MOVIE_COUNTRY, MOVIE_DB_SCORE, MOVIE_CONTENT, MOVIE_SHOW_TIME, CRT_DATE, ALT_DATE) VALUES ('aca27d78-c281-461e-9c65-f65fcb40a577', '大话西游之大圣娶亲', 'A Chinese Odyssey Part Two - Cinderella', '中国香港', '9.2', '至尊宝（周星驰 饰）被月光宝盒带回到五百年前，遇见紫霞仙子（朱茵 饰），被对方打上烙印成为对方的人，并发觉自己已变成孙悟空。', '1995-02-04', TO_DATE('2019-07-14 19:13:03', 'YYYY-MM-DD HH24:MI:SS'), TO_DATE('2019-07-14 19:13:03', 'YYYY-MM-DD HH24:MI:SS'));
